import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';

const Scorepage = () => {
    document.body.style.fontFamily = 'Nunito, sans-serif';
    document.body.style.background = "#1976D2";
    document.body.style.color = "white";

    const location = useLocation();
    const state = location.state;
    const navigate = useNavigate();

    if (!state || !state.score) {
        // If state is null or score is not available, display a message or redirect to another page
        return (
            <div>
                <h1>Score Not Available</h1>
                <Link to="/">Go Home</Link>
            </div>
        );
    }

    const { score, selectedOptions, questions } = state;

    let message;
    if (score >= 8 && score <= 10) {
        message = "Very good!";
    } else if (score >= 5 && score < 8) {
        message = "Good!";
    } else {
        message = "Better luck next time!";
    }

    const handleCheckAnswer = () => {
        navigate('/Answerpage', { state: { selectedOptions, questions } });
    };

    return (
        <>
       
        <div style={{ background: 'blue', height: '600px', width: '60%', marginTop: '150px', marginLeft: '500px', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <h1 className='text-7xl' style={{ textAlign: 'center' }}>Your Score</h1>
            <h1 className='text-8xl p-7' style={{ textAlign: 'center' }}>{score} <span className='text-6xl'>of 10</span></h1>
            <h2 className='text-4xl p-7' style={{ textAlign: 'center' }}>{message}</h2>
            <button onClick={handleCheckAnswer} className='rounded-full' style={{ padding: '10px', backgroundColor: 'green', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer', marginLeft: '400px' }}>
                Check the Answer
            </button>
            <button style={{ padding: '10px', fontSize: '16px', backgroundColor: 'red', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}>Button 3</button>
         </div>
         </>
    );
};

export default Scorepage;
