import React, { useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowRight } from '@fortawesome/free-solid-svg-icons';

const Htmlpage = () => {
    document.body.style.fontFamily='Roboto Slab, serif';
    document.body.style.background="#3d8fdb"
    document.body.style.color="white"

    const but={
        position:'relative',
        left:'1200px',
        bottom:'330px',
       
        width:'150px',
        height:'150px'
    }

    
        const [selectedOption, setSelectedOption] = useState('');

        const handleOptionChange = (event) => {
            setSelectedOption(event.target.value);
          };

  return ( 
    <>
    <div style={{background:"black",width:'80%',height:'750px',marginTop:'80px',marginLeft:'200px'}} className='rounded-full'>

        <h1 className='text-4xl ml-48  pt-28'>1. What does HTML stand for?</h1>
        <div className="flex flex-col gap-2 pt-20 ml-56 text-4xl">
      <label className="inline-flex items-center">
        <input
          type="radio"
          value="Hyper Text Markup Language"
          checked={selectedOption === "Hyper Text Markup Language"}
          onChange={handleOptionChange}
          className="form-radio text-blue-500 h-5 w-5"
        />
        <span className="ml-2">Hyper Text Markup Language</span>
      </label>

      <label className="inline-flex items-center">
        <input
          type="radio"
          value="High Tech Markup Language"
          checked={selectedOption === "High Tech Markup Language"}
          onChange={handleOptionChange}
          className="form-radio text-blue-500 h-5 w-5"
        />
        <span className="ml-2">High Tech Markup Language</span>
      </label>

      <label className="inline-flex items-center">
        <input
          type="radio"
          value="Hyperlink and Text Markup Language"
          checked={selectedOption === "Hyperlink and Text Markup Language"}
          onChange={handleOptionChange}
          className="form-radio text-blue-500 h-5 w-5"
        />
        <span className="ml-2">Hyperlink and Text Markup Language</span>
      </label>

      <label className="inline-flex items-center">
        <input
          type="radio"
          value="Home Tool Markup Language"
          checked={selectedOption === "Home Tool Markup Language"}
          onChange={handleOptionChange}
          className="form-radio text-blue-500 h-5 w-5"
        />
        <span className="ml-2">Home Tool Markup Language</span>
      </label>

      <p className="mt-20"><span style={{color:'#3d8fdb'}}>Selected Answer : </span> <span style={{color:'green'}}>{selectedOption}</span> </p>
   <button style={but} className='rounded-full'><FontAwesomeIcon icon={faArrowRight} className="text-3xl" /></button>
    </div>

    </div>
    </>
  )
}

export default Htmlpage
