import React, { useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowRight } from '@fortawesome/free-solid-svg-icons';

const Htmlpage = () => {
    document.body.style.fontFamily='Roboto Slab, serif';
    document.body.style.background="#3d8fdb"
    document.body.style.color="white"

    const but={
        position:'relative',
        left:'1200px',
        bottom:'330px',
       
        width:'100px',
        height:'200px',
        background:'#292828'
    }

    
        const [selectedOption, setSelectedOption] = useState('');

        const handleOptionChange = (event) => {
            setSelectedOption(event.target.value);
          };

          const [questions] = useState([
            {
              question: '1. What does HTML stand for?',
              options: [
                'Hyper Text Markup Language',
                'High Tech Markup Language',
                'Hyperlink and Text Markup Language',
                'Home Tool Markup Language',
              ],
            },
            {
                question: '1. What does HTML stand for?',
                options: [
                  'Hyper Text Markup Language',
                  'High Tech Markup Language',
                  'Hyperlink and Text Markup Language',
                  'Home Tool Markup Language',
                ],
              },
            // Add more questions and options as needed
          ]);

          const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

          // State to keep track of the selected option
         

            // Function to handle button click for showing the next question
  const handleNextQuestion = () => {
    // Check if an option is selected
    if (!selectedOption) {
      alert('Please select an option');
      return;
    }
    // Check if it's the last question
    if (currentQuestionIndex === questions.length - 1) {
      alert('End of questions');
      return;
    }
    // Increment the current question index
    setCurrentQuestionIndex(currentQuestionIndex + 1);
    // Clear the selected option for the next question
    setSelectedOption('');
  };

  return ( 
    <>
 <div style={{ background: 'black', width: '80%', height: '750px', marginTop: '80px', marginLeft: '200px' }} className='rounded-full'>

<h1 className='text-4xl ml-48  pt-32'>{questions[currentQuestionIndex].question}</h1>
<div className="flex flex-col gap-2 pt-20 ml-56 text-4xl">
  {questions[currentQuestionIndex].options.map((option, index) => (
    <label key={index} className="inline-flex items-center">
      <input
        type="radio"
        value={option}
        checked={selectedOption === option}
        onChange={handleOptionChange}
        className="form-radio text-blue-500 h-5 w-5"
      />
      <span className="ml-2">{option}</span>
    </label>
  ))}
  <p className="mt-20">
    <span style={{ color: '#3d8fdb' }}>Selected Answer : </span>
    <span style={{ color: 'green' }}>{selectedOption}</span>
  </p>
  <button style={but} className='rounded-full' onClick={handleNextQuestion}>
    <FontAwesomeIcon icon={faArrowRight} className="text-3xl" />
  </button>
</div>

    </div>
    </>
  )
}

export default Htmlpage
