import { useLocation } from 'react-router-dom';
import React from 'react';

const Scorepage = ({ location }) => {
    const { score, selectedOptions, questions } = location.state;

    const renderAnswer = (questionIndex, selectedOption) => {
        const correctAnswer = questions[questionIndex].answer;
        return (
            <p key={questionIndex}>
                <span>{questions[questionIndex].question}</span>:&nbsp;
                <span style={{ color: selectedOption === correctAnswer ? 'green' : 'red' }}>
                    {selectedOption === correctAnswer ? 'Correct' : 'Wrong'}
                </span>
            </p>
        );
    };

    return (
        <div>
            <h1>Score: {score}</h1>
            <h2>Answers:</h2>
            <div>
                {selectedOptions.map((option, index) => renderAnswer(index, option))}
            </div>
        </div>
    );
};

export default Scorepage;
