import React from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowRight } from '@fortawesome/free-solid-svg-icons';
import { navigate } from '@reach/router';

const Intro = () => {
    document.body.style.fontFamily='Roboto Slab, serif';
    document.body.style.background="#3d8fdb"
    document.body.style.color="white"

    
    const handleClick = () => {
        // Navigate to the desired HTML page
        navigate('/htmlpage');  // Replace '/htmlpage' with your actual HTML page route
      };


  return (
   <>
   <div style={{background:"black",width:'80%',height:'750px',marginTop:'80px',marginLeft:'200px'}} className='rounded-full'>
<h1 className='text-6xl ml-10 pl-24 pt-32' >Test :</h1>
<h1 className='text-4xl ml-48 p-4'>⭐ The test contains 20 questions and there is no time limit. </h1>

<h1 className='text-4xl ml-48 p-4'> ⭐ You will get 1 point for each correct answer. At the end of the Quiz, your total score will be displayed. </h1>
<h1 className='text-6xl mt-5 ml-10 pl-24 p-4' style={{color:'green'}}>Good Luck!</h1>
<div style={{ display: 'flex', justifyContent: 'center', }} >

  <button onClick={handleClick} className='text-4xl p-4 mt-24 bg-sky-500 hover:bg-sky-700' style={{border: '2px solid blue'}}>Start the Quiz <FontAwesomeIcon icon={faArrowRight} className="text-3xl mr-2" /></button>
</div>

   </div>
   </>
  )
}

export default Intro
