import React, { useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';

const Scorepage = () => {
    document.body.style.fontFamily = 'Nunito, sans-serif';
    document.body.style.background = "#1976D2";
    document.body.style.color = "white";

    const location = useLocation();
    const state = location.state;

    const [showQuestions, setShowQuestions] = useState(false); // State to track if questions should be displayed

    if (!state || !state.score) {
        // If state is null or score is not available, display a message or redirect to another page
        return (
            <div>
                <h1>Score Not Available</h1>
                <Link to="/">Go Home</Link>
            </div>
        );
    }

    const { score, selectedOptions, questions } = state;

    let message;
    if (score >= 8 && score <= 10) {
        message = "Very good!";
    } else if (score >= 5 && score < 8) {
        message = "Good!";
    } else {
        message = "Better luck next time!";
    }

    const handleCheckAnswer = () => {
        setShowQuestions(true); // Set showQuestions to true upon clicking the button
    };

    return (
        <div style={{ background: 'blue', width: '80%', marginTop: '80px', marginLeft: '200px' }} className='rounded-full'>
            <h1 className='text-4xl ml-48  pt-32'>Result</h1>
            <h1 className='text-8xl p-7' style={{ textAlign: 'center' }}>{score} <span className='text-6xl'>of 10</span></h1>
            <h2 className='text-4xl p-7' style={{ textAlign: 'center' }}>{message}</h2>
            {showQuestions ? ( // Render questions and answers only if showQuestions is true
                <div className="flex flex-col gap-2 pt-20 ml-56 text-4xl">
                    {questions.map((item, index) => (
                        <div key={index}>
                            <h3>{item.question}</h3>
                            <p>Selected Answer: {selectedOptions[index]}</p>
                            <p>Correct Answer: {item.answer}</p>
                        </div>
                    ))}
                    <Link to="/secondpage">
                        <button className='rounded-full' style={{ padding: '10px', backgroundColor: 'green', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}>Back to Home</button>
                    </Link>
                </div>
            ) : ( // Render button if showQuestions is false
                <button onClick={handleCheckAnswer} className='rounded-full' style={{ padding: '10px', backgroundColor: 'green', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer', marginLeft: '400px' }}>
                    Check the Answer
                </button>
            )}
        </div>
    );
};

export default Scorepage;
