import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Answerpage = () => {
    document.body.style.fontFamily='Nunito, sans-serif';
    document.body.style.background="#1976D2"
    document.body.style.color="white"

    const location = useLocation();
    const { selectedOptions, questions } = location.state;

    const calculateScore = () => {
        let correctAnswers = 0;
        let wrongAnswers = 0;
        const result = [];
        
        for (let i = 0; i < questions.length; i++) {
            const { question, answer } = questions[i];
            const selectedOption = selectedOptions[i];
            
            if (selectedOption === answer) {
                correctAnswers++;
            } else {
                wrongAnswers++;
            }

            result.push({ question, selectedOption, answer });
        }
        
        return { correctAnswers, wrongAnswers, result };
    };

    const { correctAnswers, wrongAnswers, result } = calculateScore();

    return (
        <div>
            <h1>Correct Answers: {correctAnswers}</h1>
            <h1>Wrong Answers: {wrongAnswers}</h1>
            <div>
                {result.map((item, index) => (
                    <div key={index}>
                        <h3>{item.question}</h3>
                        <p>Selected Answer: {item.selectedOption}</p>
                        <p>Correct Answer: {item.answer}</p>
                    </div>
                ))}
            </div>
            <Link to="/secondpage">Go Back</Link>
        </div>
    );
};

export default Answerpage;
