import React, { useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowRight } from '@fortawesome/free-solid-svg-icons';
import { useNavigate } from 'react-router-dom';


const Csspage = () => {
    document.body.style.fontFamily='Roboto Slab, serif';
    document.body.style.background="#3d8fdb"
    document.body.style.color="white"

    const but={
        position:'relative',
        left:'1200px',
        bottom:'330px',
       
        width:'100px',
        height:'200px',
        background:'#292828'
    }
    

    
        const [selectedOption, setSelectedOption] = useState('');

        const handleOptionChange = (event) => {
            setSelectedOption(event.target.value);
          };

          const [questions] = useState([
            {
                question: '1. What does CSS stand for?',
                options: [
                    'Cascading Style Sheets',
                    'Creative Style Sheets',
                    'Colorful Style Sheets',
                    'Computer Style Sheets',
                ],
                answer: 'Cascading Style Sheets'
            },
            {
                question: '2. Which CSS property is used to change the text color of an element?',
                options: [
                    'color',
                    'text-color',
                    'font-color',
                    'foreground-color',
                ],
                answer: 'color'
            },
            {
                question: '3. What is the correct CSS syntax to select an element with id "demo"?',
                options: [
                    '#demo',
                    '.demo',
                    'demo',
                    '*demo',
                ],
                answer: '#demo'
            },
            {
                question: '4. Which CSS property is used to control the spacing between lines of text?',
                options: [
                    'line-height',
                    'letter-spacing',
                    'word-spacing',
                    'text-spacing',
                ],
                answer: 'line-height'
            },
            {
                question: '5. What does the "float" property in CSS do?',
                options: [
                    'Allows an element to be positioned relative to its normal position',
                    'Creates a floating effect for an element',
                    'Aligns text to the left or right within its container',
                    'Specifies the distance between the borders of adjacent elements',
                ],
                answer: 'Creates a floating effect for an element'
            },
            {
                question: '6. Which CSS property is used to make text bold?',
                options: [
                    'font-weight',
                    'text-style',
                    'font-style',
                    'bold',
                ],
                answer: 'font-weight'
            },
            {
                question: '7. What is the default value of the "position" property in CSS?',
                options: [
                    'static',
                    'relative',
                    'absolute',
                    'fixed',
                ],
                answer: 'static'
            },
            {
                question: '8. Which CSS selector selects all elements that are direct children of a specified element?',
                options: [
                    '>',
                    '+',
                    '~',
                    '!',
                ],
                answer: '>'
            },
            {
                question: '9. What does the CSS property "display: none;" do?',
                options: [
                    'Hides the element completely',
                    'Changes the display to block-level',
                    'Creates a transparent background for the element',
                    'Moves the element out of the document flow',
                ],
                answer: 'Hides the element completely'
            },
            {
                question: '10. Which CSS property is used to add shadows to elements?',
                options: [
                    'box-shadow',
                    'text-shadow',
                    'element-shadow',
                    'shadow-effect',
                ],
                answer: 'box-shadow'
            }
            
            // Add more questions and options as needed
          ]);

          const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

          // State to keep track of the selected option
         

            // Function to handle button click for showing the next question
  const handleNextQuestion = () => {
    // Check if an option is selected
    if (!selectedOption) {
      alert('Please select an option');
      return;
    }
    // Check if it's the last question
    if (currentQuestionIndex === questions.length - 1) {
      alert('End of questions');
      return;
    }
    // Increment the current question index
    setCurrentQuestionIndex(currentQuestionIndex + 1);
    // Clear the selected option for the next question
    setSelectedOption('');
  };

  return ( 
    <>

 <div style={{ background: 'black', width: '80%', height: '750px', marginTop: '80px', marginLeft: '200px' }} className='rounded-full'>

<h1 className='text-4xl ml-48  pt-32'>{questions[currentQuestionIndex].question}</h1>
<div className="flex flex-col gap-2 pt-20 ml-56 text-4xl">
  {questions[currentQuestionIndex].options.map((option, index) => (
    <label key={index} className="inline-flex items-center">
      <input
        type="radio"
        value={option}
        checked={selectedOption === option}
        onChange={handleOptionChange}
        className="form-radio text-blue-500 h-5 w-5"
      />
      <span className="ml-2">{option}</span>
    </label>
  ))}
  <p className="mt-20">
    <span style={{ color: '#3d8fdb' }}>Selected Answer : </span>
    <span style={{ color: 'green' }}>{selectedOption}</span>
  </p>
  <button style={but} className='rounded-full' onClick={handleNextQuestion}>
    <FontAwesomeIcon icon={faArrowRight} className="text-3xl" />
  </button>
</div>

    </div>
    </>
  )
}

export default Csspage
