import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';

const Scorepage = () => {
    document.body.style.fontFamily = 'Nunito, sans-serif';
    document.body.style.background = "#1976D2";
    document.body.style.color = "white";

    const location = useLocation();
    const state = location.state;
    const navigate = useNavigate();

    if (!state || !state.score) {
        // If state is null or score is not available, display a message or redirect to another page
        return (
            <div>
                <h1>Score Not Available</h1>
                <Link to="/">Go Home</Link>
            </div>
        );
    }

    const { score, selectedOptions, questions } = state;

    let message;
    if (score >= 8 && score <= 10) {
        message = "Very good!";
    } else if (score >= 5 && score < 8) {
        message = "Good!";
    } else {
        message = "Better luck next time!";
    }

    const handleCheckAnswer = () => {
        navigate('/Answerpage', { state: { selectedOptions, questions } });
    };

    return (
        <div style={{ background: 'blue', height: '600px', width: '60%', marginTop: '150px', marginLeft: '450px' }}>
            <h1 className='text-7xl pt-24 p-7' style={{ textAlign: 'center' }}>Result</h1>
            <h1 className='text-8xl p-7' style={{ textAlign: 'center' }}>{score} <span className='text-6xl'>of 10</span></h1>
            <h2 className='text-4xl p-7' style={{ textAlign: 'center' }}>{message}</h2>
            <button onClick={handleCheckAnswer} className='text-2xl' style={{ padding: '10px', marginLeft: '400px', backgroundColor: 'green', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer', marginRight: '20px' }}>
                Check the Answer
            </button>
            <Link to="/secondpage">
                <button className='text-2xl' style={{ padding: '10px', backgroundColor: 'green', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}>
                    Back to Home
                </button>
            </Link>
        </div>
    );
};

export default Scorepage;
